PAGE.CONTENT:
Probably the most successful script I have ever written: all project leaders of the department used it to handle the DDTS records much more quickly than by the usual DDTS interfaces.

#!/usr/bin/perl -w

use strict;



# ajouter la severite du SPR dans le resume HTML

# verifier que l'enclosure n'est pas deja presente





# syntax:

#

# #$SPR_ID indicates the beginning od SPR SPR_ID

# it is followed by the enclosure text

# and ends by some actions which can be

# #>F toto      SPR will be forwarded to bucket toto

# #>C foobar    SPR is closed and foobar is the verifier





#--------------------------------------------------------------------------



# file containing the SPR data

my $datafile = "/srv/gxre-napp3/vol4/mazurla/last_srb";

#my $datafile = "/srv/gxre-napp3/vol4/mazurla/report_srb";



# name of the SRB

#my $srbname = "AW_in dispatch SRB";

my $srbname = "AW4.2 init SRB";

#my $srbname = "AW_Linux M4 postpone SRB";

#my $srbname = "sev12";

#my $srbname = "AW_Linux final M4 SRB";

#my $srbname = "AW4.2 M4 postpone SRB";



# date of the SRB

my $srbdate = "10Nov03";



# current milestone and version (to close the SPR)

my $close_milestone = "M4-HIGH";

my $close_version = "AW4.2_03.10_EXT";



# attendees of the SRB

my @srbattend =(

#"J. Aguirre         OLC team",

"G. Bernard         AW4.2 LSD",

"F. Bloise          AW4.2 LPI",

#"L. Bonardi         IB TEAM",

#"M. Colin           IB team",

#"B. Cousseau        AW4.1 LPI",

#"G. Deharbe         OLC team",

#"J. Descheirder     AW marketing manager",

#"L. Djemli          SRE team",

#"B. Dupont          SRE team",

#"H. Foucault        Liberty team",

#"J.L. Houvenagel    IB manager",

#"G. Lemaire         IB team",

#"M. Letonai         AW team",

#"J. Markvicka       AW marketing",

#"S. Nichols         AW marketing",

#"F. Peter           AW team",

#"F. Sureda          Liberty team",

#"P. Ventura         IB team",

#"P. Vessieres       IB team",

#"B. Le Corgne       RWS LSD",

#"A. Desolle         AW4.2 team",

"L. Mazure          AW4.2 team",

);



#--------------------------------------------------------------------------



# file containing the HTML summary

my $htmlfile = "/tmp/srb.html";



# prefix names for the files

my $tmpfix = "/tmp/__my__srb__".$$;



# SPR list

my @sprlist;



# clean up

sub clean_up {

    my $s;

    for $s (@sprlist) {

        my $file = $tmpfix.$s;

        unlink $file or die("failed to remove file $file ($!)")

    }

}

$SIG{"INT"} =  \&clean_up;



# convert a line to HTML

sub to_html {

    my ($line) = @_;

    $line =~ s/</\&LT;/g;

    $line =~ s/>/\&GT;/g;

    $line =~ s~(((BUC|CTC|EUR|GSA|IIS|MRI|NPS|XRY|SVC)ge)|(YMS(mr|ct)))[0-9][0-9][0-9][0-9][0-9]~<A HREF="http://frbucdts01medge.euro.med.ge.com/ddts/ddts_main?LastForm=DumpBug\&bug_id=$&" TARGET="_blank">$&</A>~g;

    #$line =~ s~J. Aguirre~<A HREF="ldap://3.57.24.56/cn%3DFLOREJO%2Ccn%3DRecipients%2Cou%3DGEMSE%2Co%3DGE" TARGET="_blank">$&</A>~g;

    #$line =~ s~C\. Allory~<A HREF="ldap://3.57.24.56/cn%3DALLORCA%2Ccn%3DRecipients%2Cou%3DGEMSE%2Co%3DGE" TARGET="_blank">$&</A>~g;

    #$line =~ s~G\. Bernard~<A HREF="ldap://3.57.24.56/cn%3DBERNAGU%2Ccn%3DRecipients%2Cou%3DGEMSE%2Co%3DGE" TARGET="_blank">$&</A>~g;

    #$line =~ s~F\. Bloise~<A HREF="ldap://3.57.24.56/cn%3DBLOISFR%2Ccn%3DRecipients%2Cou%3DGEMSE%2Co%3DGE" TARGET="_blank">$&</A>~g;

    #$line =~ s~L\. Bonardi~<A HREF="ldap://3.57.24.56/cn%3DBONARLA%2Ccn%3DRecipients%2Cou%3DGEMSE%2Co%3DGE" TARGET="_blank">$&</A>~g;

    #$line =~ s~M\. Colin~<A HREF="ldap://3.57.24.56/cn%3DCOLINMI%2Ccn%3DRecipients%2Cou%3DGEMSE%2Co%3DGE" TARGET="_blank">$&</A>~g;

    #$line =~ s~B\. Cousseau~<A HREF="ldap://3.57.24.56/cn%3DCOUSSBE%2Ccn%3DRecipients%2Cou%3DGEMSE%2Co%3DGE" TARGET="_blank">$&</A>~g;

    #$line =~ s~J\. Descheirder~<A HREF="ldap://3.57.24.56/cn%3DDESCHJE%2Ccn%3DRecipients%2Cou%3DGEMSE%2Co%3DGE" TARGET="_blank">$&</A>~g;

    #$line =~ s~G\. Deharbe~<A HREF="ldap://3.57.24.56/cn%3DDEHARGI%2Ccn%3DRecipients%2Cou%3DGEMSE%2Co%3DGE" TARGET="_blank">$&</A>~g;

    #$line =~ s~L\. Djemli~<A HREF="ldap://3.57.24.56/cn%3DDJEMLLA%2Ccn%3DRecipients%2Cou%3DGEMSE%2Co%3DGE" TARGET="_blank">$&</A>~g;

    #$line =~ s~B\. Dupont~<A HREF="ldap://3.57.24.56/cn%3DDUPONBE%2Ccn%3DRecipients%2Cou%3DGEMSE%2Co%3DGE" TARGET="_blank">$&</A>~g;

    #$line =~ s~E\. Gleye~<A HREF="ldap://3.57.24.56/cn%3DGLEYEEM%2Ccn%3DRecipients%2Cou%3DGEMSE%2Co%3DGE" TARGET="_blank">$&</A>~g;

    #$line =~ s~J\.L\. Houvenagel~<A HREF="ldap://3.57.24.56/cn%3DHOUVEJE%2Ccn%3DRecipients%2Cou%3DGEMSE%2Co%3DGE" TARGET="_blank">$&</A>~g;

    #$line =~ s~P\. Jouenne~<A HREF="ldap://3.57.24.56/cn%3DJOUENPA%2Ccn%3DRecipients%2Cou%3DGEMSE%2Co%3DGE" TARGET="_blank">$&</A>~g;

    #$line =~ s~G\. Lemaire~<A HREF="ldap://3.57.24.56/cn%3DLEMAIGW%2Ccn%3DRecipients%2Cou%3DGEMSE%2Co%3DGE" TARGET="_blank">$&</A>~g;

    #$line =~ s~M\. Letonai~<A HREF="ldap://3.57.24.56/cn%3DLETONMA%2Ccn%3DRecipients%2Cou%3DGEMSE%2Co%3DGE" TARGET="_blank">$&</A>~g;

    #$line =~ s~J\. Markvicka~<A HREF="ldap://3.57.24.56/cn%3DMS04453%2Ccn%3DRecipients%2Cou%3DGEMSAM%2Co%3DGE" TARGET="_blank">$&</A>~g;

    #$line =~ s~L\. Mazure~<A HREF="ldap://3.57.24.56/cn%3DMAZURLA%2Ccn%3DRecipients%2Cou%3DGEMSE%2Co%3DGE" TARGET="_blank">$&</A>~g;

    #$line =~ s~G\. Moris~<A HREF="ldap://3.57.24.56/cn%3DMORISGI0F3A5962%2Ccn%3DOther%2Cou%3DGEMSE%2Co%3DGE" TARGET="_blank">$&</A>~g;

    #$line =~ s~S\. Nichols~<A HREF="ldap://3.57.24.56/cn%3DNICHOST0F3A5962%2Ccn%3DOther%2Cou%3DGEMSE%2Co%3DGE" TARGET="_blank">$&</A>~g;

    #$line =~ s~O.Pizzato~<A HREF="ldap://3.57.24.56/cn%3DPIZZAOL%2Ccn%3DRecipients%2Cou%3DGEMSE%2Co%3DGE" TARGET="_blank">$&</A>~g;

    #$line =~ s~E\. Stefani~<A HREF="ldap://3.57.24.56/cn%3DSTEFAER928FA82F%2Ccn%3DOther%2Cou%3DGEMSE%2Co%3DGE" TARGET="_blank">$&</A>~g;

    #$line =~ s~F. Sureda~<A HREF="ldap://3.57.24.56/cn%3DSUREDFR%2Ccn%3DRecipients%2Cou%3DGEMSE%2Co%3DGE" TARGET="_blank">$&</A>~g;

    #$line =~ s~M. Trost~<A HREF="ldap://3.57.24.56/cn%3DTROSTMI%2Ccn%3DRecipients%2Cou%3DGEMSE%2Co%3DGE" TARGET="_blank">$&</A>~g;

    #$line =~ s~P\. Ventura~<A HREF="ldap://3.57.24.56/cn%3DVENTUPA%2Ccn%3DRecipients%2Cou%3DGEMSE%2Co%3DGE" TARGET="_blank">$&</A>~g;

    #$line =~ s~P\. Vessieres~<A HREF="ldap://3.57.24.56/cn%3DVESSIPH%2Ccn%3DRecipients%2Cou%3DGEMSE%2Co%3DGE" TARGET="_blank">$&</A>~g;

    #$line =~ s~G\. Zablot~<A HREF="ldap://3.57.24.56/cn%3DZABLOGE%2Ccn%3DRecipients%2Cou%3DGEMSE%2Co%3DGE" TARGET="_blank">$&</A>~g;

    return $line;

}



open(DATA,$datafile) or die("cannot open file $datafile ($!)"); 

open(HTML,"> ".$htmlfile) or die("cannot open file $datafile ($!)"); 



print HTML "<HTML>";

print HTML "<HEAD><TITLE>";

print HTML $srbname." -- ".$srbdate;

print HTML "</TITLE></HEAD>";

print HTML "<BODY BGCOLOR='white'>";



print HTML to_html("These SPRs have been reviewed during the $srbname ($srbdate)"), "<BR>\n";

print HTML to_html("The attendees were:"), "<BR>\n";

for $a (@srbattend) {

    print HTML to_html($a), "<BR>\n";

}

print HTML "<BR>", to_html("The name of the enclosure is \"$srbname\"."), "<BR><BR>\n";



my $err = 0;

my $line = <DATA>;

while ( $line ) {

    chomp $line;

    if ( $line !~ /^#\$([A-Z][A-Z][A-Z][a-z][a-z][0-9][0-9][0-9][0-9][0-9])$/ ) {

        warn("bad SPR ID ($line) at line $. of $datafile");

        $err++;

    }

    my $spr = $1;

    push @sprlist, $spr;

    print HTML to_html($spr);



    # --- check severity

    if (!open(BUGSEV,"/ddts/bin/bugval -i $spr Severity |")) {

      warn("failed to get severity of $spr ($!)");

      $err++;

    }

    my $severity;

    if (!($severity = <BUGSEV>)) {

        warn("failed to get severity of $spr");

        $err++;

    }

    if ( $severity == "1" || $severity == "2" ) {

        print HTML "<FONT STYLE='background-color: #ff0000'>", to_html("  sev=$severity"), "</FONT><BR>\n";

    } else {

        print HTML to_html("  sev=$severity"), "<BR>\n";

    }

    close(BUGSEV);



    # --- check milestone

    if (!open(BUGMIL,"/ddts/bin/bugval -i $spr Milestone |")) {

        warn("failed to get milestone of $spr ($!)");

        $err++;

    }

    my $milestone;

    if (!($milestone = <BUGMIL>)) {

        warn("failed to get milestone of $spr");

        $err++;

    }

    chomp $milestone;

    close(BUGMIL);



    # --- check headline

    if (!open(BUGVAL,"/ddts/bin/bugval -i $spr Headline |")) {

        warn("failed to get headline of $spr ($!)");

        $err++;

    }

    my $headline;

    if (!($headline = <BUGVAL>)) {

        warn("failed to get headline of $spr");

        $err++;

    }

    print HTML "<B>",to_html($headline),"</B><BR>\n";

    close(BUGVAL);



    # --- check enclosures

    if (!open(ENCLOSURES,"/ddts/bin/dumpbug -R $spr | grep 'Related-file::' | sed 's/.*:://' |")) {

        warn("failed to get enclosures of $spr ($!)");

        $err++;

    }

    my $enclosures;

    while ($enclosures = <ENCLOSURES>) {

        chomp $enclosures;

        if ( " $srbname" eq "$enclosures" ) {

            warn("$spr has already an enclosure '$srbname'");

            $err++;

        }

    }

    close(ENCLOSURES);



    # --- check presence

    my $file = $tmpfix.$spr;

    if ( -f $file ) {

        warn("SPR $spr already present");

        $err++;

    }



    open(FILE,"> ".$file) or die("cannot open file $file ($!)"); 

    print FILE "#!/bin/csh\n\n";

    print FILE "setenv PATH \${PATH}:/ddts/bin\n\n";

    print FILE "ddtslock $spr\n";

    print FILE "if (\$status) then\n";

    print FILE "  echo 'failed to get lock on $spr'\n";

    print FILE "  exit 1\n";

    print FILE "endif\n";

    print FILE "echo $spr locked\n\n";

    print FILE "cat << __EOF__ > ".$file."_text\n";

    print FILE "This SPR has been reviewed during the $srbname ($srbdate)\n";

    print FILE "The attendees were:\n";

    for $a (@srbattend) {

        print FILE $a, "\n";

    }

    print FILE "\n";

    my $data;

    while ( defined($data = <DATA>) && ($data !~ /^#\$/) && ($data !~ /^#>/) ) {

        chomp $data;

        if ( $data =~ /->/ ) {

            my $amil = $data;

            $amil =~ s/-> (M[A-Z0-9]+), ([A-Z]+)/$1-$2/;

            $amil =~ s/^-> VERYHIGH$/VERYHIGH/;

            $amil =~ s/^-> HIGH$/HIGH/;

            $amil =~ s/^-> MIDDLE$/MIDDLE/;

            $amil =~ s/^-> LOW$/LOW/;

            $amil =~ s/^-> VERYLOW$/VERYLOW/;

            $amil =~ s/^-> ASAP$/ASAP/;

            $amil =~ s/^-> EVOL$/EVOL/; # for AiM++

            if ( $amil ne $milestone ) {

                warn("$spr is assigned for '$milestone', but SRB indicates '$amil'");

                $err++;

     }

        }

        print HTML to_html($data), "<BR>\n";

        print FILE $data, "\n";

    }

    if ( defined($data) && $data =~ /^#>/ ) {

        if ( $data =~ /^#>F (.*)$/ ) {

            if (system("cat /ddts/proj.info/$1 | grep 'Class: cspr' > /dev/null" )) {

                warn("$1 is an unknown bucket in class cspr for $spr");

                $err++;

     }

            print FILE "\n-> forwarded in $1 bucket\n";

        } elsif ( $data =~ /^#>C (.*)$/ ) {

            if (system("echo $1 | /ddts/bin/goodusers > /dev/null")) {

                warn("$1 is an unknown user for $spr");

                $err++;

     }

            print FILE "\n-> closed\n";

        } else {

            warn("unrecognized command $spr");

            $err++;

        }

    }

    print FILE "\n";

    print FILE "L. Mazure (".localtime().")\n";

    print FILE "__EOF__\n\n";

    if ( defined($data) && $data =~ /^#>F (.*)$/ ) {

        # forward

        print FILE "batchbug -i $spr  -e ".$file."_text '$srbname' -t /ddts/class/software/batchbug/F New-class 'cspr' Forwarded-to '$1'\n";

        print FILE "if (\$status) then\n";

        print FILE "  echo 'failed to forward $spr to AW4.1'\n";

        print FILE "  exit 1\n";

        print FILE "endif\n";

        print FILE "echo $spr forwarded to $1\n\n";

        print HTML "<I>",to_html("-> forwarded in $1 bucket"),"</I><BR>\n";

        $data = <DATA>;

    } elsif ( defined($data) && $data =~ /^#>C (.*)$/ ) {

        # close (take care to wait for the assignment to be done before resolving)

        print FILE "set stamp=`bugval -i $spr timestamp`\n\n";

        print FILE "batchbug -i $spr  -e ".$file."_text '$srbname' -t /ddts/class/cspr/batchbug/A Engineer 'mazurla' milestone '$close_milestone' ass_to_verify '$1'\n";

        print FILE "if (\$status) then\n";

        print FILE "  echo 'failed to assign $spr'\n";

        print FILE "  exit 1\n";

        print FILE "endif\n";

        print FILE "echo $spr assigned\n\n";

        print FILE "cat << __EOF__ > ".$file."_resol\n";

        print FILE "            'NO FIX' ENCLOSURE\n";

        print FILE "            --------------------\n";

        print FILE "The below fields are REQUIRED\n";

        print FILE "\n";

        print FILE "PROBLEM SUMMARY:\n";

        print FILE "\n";

        print FILE "\n";

        print FILE "DETAILED INVESTIGATION DESCRIPTION:\n";

        print FILE "\n";

        print FILE "\n";

        print FILE "REASON FOR 'NO FIX':\n";

        print FILE "  see '$srbname' enclosure\n";

        print FILE "__EOF__\n\n";

        print FILE "echo -n 'waiting for $spr update'\n";

        print FILE "while ( \$stamp == `bugval -i $spr timestamp` )\n";

        print FILE "  echo -n '.'\n";

        print FILE "  sleep 1\n";

        print FILE "end\n";

        print FILE "echo 'done'\n\n";

        print FILE "batchbug -i $spr  -e ".$file."_resol 'Resolution_no_fix' -t /ddts/class/cspr/batchbug/R resolver_id 'mazurla' action_taken 'nofix' resolved_sw '$close_version' resolution 'no action taken'\n";

        print FILE "if (\$status) then\n";

        print FILE "  echo 'failed to resolve $spr'\n";

        print FILE "  exit 1\n";

        print FILE "endif\n";

        print FILE "echo $spr resolved\n\n";

        print HTML "<I>",to_html("-> closed (milestone=$close_milestone, version=$close_version, verifier=$1)"),"</I><BR>\n";

        print FILE "rm ".$file."_resol\n\n";

        $data = <DATA>;

    } else {

        # add enclosure

        print FILE "batchbug -i $spr -e ".$file."_text '$srbname'\n";

        print FILE "if (\$status) then\n";

        print FILE "  echo 'failed to get attach enclosure to $spr'\n";

        print FILE "  exit 1\n";

        print FILE "endif\n";

        print FILE "echo $spr has now an enclosure\n\n";

    }

    print FILE "rm ".$file."_text\n\n";

    print HTML to_html(""), "<BR>";

    print FILE "ddtslock -u $spr\n";

    print FILE "if (\$status) then\n";

    print FILE "  echo 'failed to release lock on $spr'\n";

    print FILE "  exit 1\n";

    print FILE "endif\n";

    print FILE "echo $spr unlocked\n\n";

    close(FILE) or die("cannot close file $file ($!)");

    chmod 0700,$file or die("cannot chmod file $file ($!)");

    $line = $data;

}



print HTML "</BODY>\n";

print HTML "</HTML>\n";



close(DATA);

close(HTML);



if ($err ne 0) {

    my $s;

    for $s (@sprlist) {

        my $file = $tmpfix.$s;

        unlink $file;

    }

    die("$err error(s) found, cannot process commands");

}



my @cmds = ("netscape", "-remote", "file:".$htmlfile);

system(@cmds) == 0 or die("failed to communicate with Netscape");



print "Do you accept this? (Y|N)";

my $answer=<STDIN>;

chomp $answer;



if ( $answer eq "Y" ) {

    my $s;

    for $s (@sprlist) {

        my $file = $tmpfix.$s;

        @cmds = ("$file");

        system(@cmds) == 0 or die("failed to update SPR $s");

        unlink $file or die("failed to remove file $file ($!)")

    }

} else {

    clean_up();

}
