CONTENT.CODESAMPLE:
#!/usr/bin/perl -w

use strict;



use IO::Socket;



my $port = "512";

my $host = "3.249.12.132";



my $repl;

my $socket = IO::Socket::INET->new(PeerAddr => $host,PeerPort => $port,Proto => "TCP") || die "socket ($!)";

print "socket created\n";



my $req = "HELO a\r\n";



syswrite($socket,$req,length($req));

$repl = <$socket>

print "server replied \"${repl}\"";



close $socket;
